const styles = {
  container: {display:'flex', justifyContent:'space-between', backgroundColor:'#282c34', color:'white', minHeight:'100vw', padding:'5%'},
  filterContainer: {display:'flex', flexDirection:'column', width:'20%', padding:'2%', margin:'2%', backgroundColor: '#49515f', height:'60vw'},
  displayContainer: {
    display:'flex',
    flexWrap:'wrap',
    width:'80%',
    margin:'2%',
    alignContent:'baseline'
  },
  chocolate: {
    display:'flex', 
    flexDirection:'column', 
    justifyContent:'space-between', 
    backgroundColor:'#49515f', 
    padding:'2%', 
    margin:'1%', 
    border:'1px solid black', 
    height:'300px', 
    width:'300px'
  },
  chocolateInfo:{display:'flex', justifyContent:'space-between'}
}
function MainPage() {
    const [renderChocolates, setRenderChocolates] = useState(chocolates);
    const RenderBrand =() =>{
        const allBrands = chocolates.map(obj=>obj.brand);
        const uniqueBrands = [];
        allBrands.map(ele=>{
            if(!uniqueBrands.includes(ele))
            {
                uniqueBrands.push(ele);
            }
        });  
        return uniqueBrands.map(ele=>{
              return <span><input type="checkbox" value="xyz" 
              onClick={()=>filterBrand(ele)}/>{ele}</span>
        });
    };

    const RenderCategory = () =>{
        const allCategory = chocolates.map(obj=>obj.category);
        const uniqueCategory = [... new Set(allCategory)];

        return uniqueCategory.map(ele=>{
            return <span><input type="checkbox" value="xyz" 
            onClick={()=>filterCategory(ele)}/>{ele}</span>
        });
    };

    
    const RenderData = () =>{
        return renderChocolates.map(ele=>{
           return(
                <div style={styles.chocolate}>
                <img src={ele.imageUrl}/>
                <span style={styles.chocolateInfo}>
                    <span>INR {ele.price}</span>
                    <span>{ele.name}</span>
                </span>
                </div>
           )
        });
    };

    
    
    const filterByPrice = (min, max) =>{
        const arr =  chocolates.filter(obj=>{
            if(obj.price>=min && obj.price<=max)
            {
                return obj;
            }          
        })
        setRenderChocolates(arr);
    };
    

    const filterBrand = (brandName) =>{
        const arr = chocolates.filter(obj=>{
            if (obj.brand === brandName){
                return obj;
            } 
        })
        setRenderChocolates(arr);
    };

    const filterCategory = (categoryName) =>{
        const arr = chocolates.filter(obj=>{
            if (obj.category === categoryName){
                return obj;
            } 
        })
        setRenderChocolates(arr);
    };

  return (

    <div style={styles.container}>
      <div style={styles.filterContainer}>
            <h3>FILTERS</h3>
            <label style={{padding: '5%', color:'wheat'}}>Brand:</label>
            {RenderBrand()}

            <label style={{padding: '5%', color:'wheat'}}>Category:</label>
            {RenderCategory()}


            <label style={{padding: '5%', color:'wheat'}}>Price:</label>
            <span><input type="checkbox" value="xyz" onClick={()=>filterByPrice(1,20)}/>1-20</span>
            <span><input type="checkbox" value="az" onClick={()=>filterByPrice(20,50)}/>20-50</span>
            <span><input type="checkbox" value="asz" onClick={()=>filterByPrice(50,100)}/>50-100</span>
            <span><input type="checkbox" value="xsaz" onClick={()=>filterByPrice(100,200)}/>100-200</span>
            <span><input type="checkbox" value="xsayz" onClick={()=>filterByPrice(200,5000)}/>200+</span>
        </div>

        <div style={styles.displayContainer}>
            {RenderData()}
        </div>
      
    </div>
  );
}

export default MainPage;
